import React from 'react';
import PropTypes from 'prop-types';

import { states as recorderStates } from '../constants/recorder';

const ActionsPanel = ({
  pauseRecording,
  recorderState,
  resumeRecording,
  startRecording,
  stopRecording
}) => (
  <>
    <button
      onClick={recorderState === recorderStates.PAUSED
        ? resumeRecording
        : startRecording
      }
      disabled={recorderState === recorderStates.RECORDING}
    >
      Record
    </button>
    <button
      onClick={pauseRecording}
      disabled={
        recorderState === recorderStates.PAUSED ||
        recorderState === recorderStates.INACTIVE
      }
    >
      Pause
    </button>
    <button
      onClick={stopRecording}
      disabled={recorderState === recorderStates.INACTIVE}
    >
      Stop
    </button>
  </>
);

ActionsPanel.propTypes = {
  pauseRecording: PropTypes.func,
  recorderState: PropTypes.string,
  resumeRecording: PropTypes.func,
  startRecording: PropTypes.func,
  stopRecording: PropTypes.func
};

export default ActionsPanel;
