import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import identicon from 'identicon';

const Recording = ({ recording }) => {
  // ðŸ›  Should move to a useVotes hook to manage votes incoming from recordings.
  const [hasVoted, setHasVoted] = useState(false);
  const [upVotes, setUpVotes] = useState(0);
  const [avatar, setAvatar] = useState();

  const vote = () => {
    const totalVotes = hasVoted
      ? upVotes - 1
      : upVotes + 1;

    setHasVoted(!hasVoted);
    setUpVotes(totalVotes);
  };

  const getAvatar = () => {
    identicon.generate({
      id: 'ajido',
      size: 150
    }, (_, buffer) => {
      // if (err) throw err
      setAvatar(buffer);

      return avatar;
    });
  };

  return (
    <div>
      <div>
        <div id="identicon" src={getAvatar()} />
        <audio src={recording} key={recording} controls />
      </div>
      {/* <button>Reply</button> */}
      <button onClick={vote}>
        {`${upVotes} `}
        <FontAwesomeIcon
          icon={["far", "thumbs-up"]}
          color={`${hasVoted ? 'green' : 'red'}`}
        />
      </button>
    </div>
  )
};

Recording.propTypes = {
  recording: PropTypes.string
};

export default Recording;
